const i=document.querySelector("nav ul li.active");i!==null&&i.scrollIntoView({behavior:"smooth",block:"end"});let l=(t="aside.sidebar")=>{document.querySelector(t).classList.toggle("hidden")};document.addEventListener("click",t=>{const n=document.querySelector("aside.sidebar");let e=t.target;do{if(e==document.querySelector("#mobileMenuBtn"))return l();if(e==n)return;e=e.parentNode}while(e);n.classList.contains("hidden")||n.classList.add("hidden")});
